#installs the burnt toast module if it is not already
if (Get-InstalledModule -Name BurntToast) {
} else {
    Install-Module BurntToast -Force
}

$burnttoastcmd = "powershell.exe -WindowStyle hidden -NonInteractive -ExecutionPolicy Bypass -NoLogo -NoProfile  -Command `"& '%1'.Replace('powershell://', '').Trim('/')"""
$protocolreg = 'Windows Registry Editor Version 5.00

[HKEY_CLASSES_ROOT\powershell]
@="URL:PowerShell Protocol"
"URL Protocol"=""

[HKEY_CLASSES_ROOT\powershell\DefaultIcon]
@="powershell.exe,1"

[HKEY_CLASSES_ROOT\powershell\shell]

[HKEY_CLASSES_ROOT\powershell\shell\open]

[HKEY_CLASSES_ROOT\powershell\shell\open\command]
@="C:\\Test\\Protocol Registration\\BurntToast.cmd %1"
'
$rebootb64 = ""
$reboot = "restart-computer"
$filename = "C:\Test\rebootfinal.exe"

# Test to see if scripts folder exists if not creates it and the registry files
if ((Test-Path -Path "C:/Test/Protocol Registration") -eq $false) { #change to scripts
    New-Item -Path "C:/Test/" -Name "Protocol Registration" -ItemType "Directory" #creates folder called "Test" and a subfolder called "Protocol Registration"
    Set-Content -Path "C:/Test/Protocol Registration/BurntToast.cmd" -Value $burnttoastcmd #creates file called "BurntToast.cmd" within Protocol Registration
    Set-Content -Path "C:/Test/Protocol Registration/register-protocol.reg" -Value $protocolreg #creates file called "register-protocol.reg" within Protocol Registration
} elseif ((Test-Path -Path "C:/Test/Protocol Registration/register-protocol.reg") -or (Test-Path -Path "C:/Test/Protocol Registration/BurntToast.cmd") -eq $false) { ##Get-child???? try it
    Set-Content -Path "C:/Test/Protocol Registration/BurntToast.cmd" -Value $burnttoastcmd #creates file called "BurntToast.cmd" within Protocol Registration
    Set-Content -Path "C:/Test/Protocol Registration/register-protocol.reg" -Value $protocolreg #creates file called "register-protocol.reg" within Protocol Registration
} else {}

#check to see if HCKR exist, if not creates it
if(Get-PSDrive HKCR -ErrorAction SilentlyContinue) {
} else {
    New-PSDrive -Name HKCR -PSProvider Registry -Root HKEY_CLASSES_ROOT | Out-Null 
}

#check to see if registry key exists, if not creates it using "register-protocol.reg" defined previously
if ((Test-Path HKCR:\powershell\shell\open\command) -eq $false) {
    reg import "C:\Test\Protocol Registration\register-protocol.reg"
} else {}

#check to see if the reboot scripts exist, if not creates them
if ((Test-Path -Path "C:\Test\rebootfinal.exe") -eq $false) {
    $bytes = [Convert]::FromBase64String($rebootb64)
    [IO.File]::WriteAllBytes($filename, $bytes)
} else {}
if ((Test-Path -Path "C:\Test\reboot.ps1") -eq $false) {
    Set-Content -Path "C:\Test\reboot.ps1" -Value $reboot
} else {}

if (Get-ScheduledTask -TaskName 'reboot script' -ErrorAction SilentlyContinue) {
} else {
    $time = New-ScheduledTaskTrigger -At 14:30 -Daily
    $action1 = New-ScheduledTaskAction -Execute 'C:\Test\rebootfinal.exe'

    Register-ScheduledTask -Action $action1 -Trigger $time -TaskName "Reboot Script" -Description "Runs script that asks to reboot the computer"
}